	// 加载更多
	
	load_list_view: function(url, selector, container, start_page, callback) {
	    
	    if (!selector.attr('id')) {
	        return false;
	    }

	    if (!start_page) {
	        start_page = 0
	    }

	    // 把页数绑定在元素上面

	    if (selector.attr('data-page') == undefined) {
	        selector.attr('data-page', start_page);
	    } else {
	        selector.attr('data-page', parseInt(selector.attr('data-page')) + 1);
	    }

	    selector.bind('click', function() {
	        var _this = $(this);
	        var spinner = $('<div class="spinner"><div class="bounce1"></div><div class="bounce2"></div><div class="bounce3"></div></div>');
	        spinner.insertBefore(_this.hide());

	        $.get(url + '__page-' + _this.attr('data-page'), function(result) {
	        	var complete = false;
	            spinner.remove();
	            _this.show();

	            if ($.trim(result) != '') {
	                if (_this.attr('data-page') == start_page && _this.attr('auto-load') != 'false') {
	                    container.html(result);
	                } else {
	                    container.append(result);
	                }

	                // 页数增加1

	                _this.attr('data-page', parseInt(_this.attr('data-page')) + 1);
	            } else {

	                //没有内容

	                if (_this.attr('data-page') == start_page && _this.attr('auto-load') != 'false') {
	                    container.html('<p class="c-gray text-center p-t-15 p-b-15">' + '没有内容' + '</p>');
	                }

	                complete = true;
	                _this.addClass('disabled').unbind('click').bind('click', function(e){
	                	e.preventDefault();
	                });
	            }

	            if (callback != null) {
	                callback(_this, complete);
	            }
	        });

	        return false;
	    });

	    // 自动加载

	    if (selector.attr('auto-load') != 'false') {
	        selector.click();
	    }
	},

